import React, { useEffect, useRef, memo, FC } from 'react';

import styles from './Main.module.css';
import togetherImg from "../assets/images/together.png";

// アイコンフォルダの画像を全部読み込む
const iconsContext = require.context('../assets/stackIcons', false, /\.(png|jpe?g|svg)$/);

const techIcons = iconsContext.keys().map((key: string) => {
  const src = iconsContext(key);
  const fileName = key.replace('./', '').replace(/\.[^/.]+$/, '');
  return { src, alt: fileName };
});


type Props = {
  canvasWidth: number; //canvas幅
  canvasHeight: number; //canvas高さ
  x: number; //矩形始点x
  y: number; //矩形始点y
  rectWidth: number; //矩形幅
  rectHeight: number; //矩形高さ
};


const Main: React.FC = () => {
  return (
    <main>
      <section className={styles.titleSection}>
      <h1>Piyoppi</h1>
      <p>Portfolio</p>
        <img
          src={togetherImg}
          alt="背景画像"
          className={styles.backgroundImage}
        />
      </section>

      <section className={styles.aboutSection}>
        <h2>About</h2>
        <div className={styles.textContainer}>
          <div className={styles.description}>
            愛知工業大学３年生
          </div>
        </div>
      </section>

      <section className={styles.stackSection}>
        <h2>Tech Stack</h2>
        <div className={styles.textContainer}>
          <div className={styles.description}>
            <a>
              <div className={styles.iconContainer}>
                {techIcons.map((icon: { src: string; alt: string }, index: number) => (
                  <img key={index} src={icon.src} alt={icon.alt} className={styles.icon} />
                ))}
              </div>
            </a>
          </div>
        </div>
      </section>
    </main>
  );
}

export default Main;
