import React, { CSSProperties, useEffect, useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

import styles from './Main.module.css';
import togetherImg from "../assets/images/together.png";

//制作のインポート
import BBKeyProduct from '../Products/BBKeyProducts';
import BlenderProduct from '../Products/BlenderProducts';
import AITBomberProduct from '../Products/AITBomberProduct';

// アイコンフォルダの画像を全部読み込んで全部表示（自動）
const iconsContext = require.context('../assets/stackIcons', false, /\.(png|jpe?g|svg)$/);

type TechIcon = {
  src: string;
  alt: string;
};

const techIcons: TechIcon[] = iconsContext.keys().map((key) => {
  const src = iconsContext(key) as string;
  const fileName = key.replace('./', '').replace(/\.[^/.]+$/, '');
  return { src, alt: fileName };
});

const CONTENT_WIDTH = 1280;
const CONTENT_HEIGHT = 1024;

const useWindowScale = () => {
  const [scale, setScale] = useState(1);

  useEffect(() => {
    function handleResize() {
      const windowWidth = window.innerWidth;
      const windowHeight = window.innerHeight;

      const scaleX = windowWidth / CONTENT_WIDTH;
      const scaleY = windowHeight / CONTENT_HEIGHT;

      // 横幅と高さの縮小比率の中で最小のものを選ぶ
      const dynamicScale = Math.min(scaleX, scaleY);

      setScale(dynamicScale);
    }

    // 初回ロード時のサイズに基づいてスケーリング
    handleResize();

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return { scale };
};

const Main: React.FC = () => {
  const { scale } = useWindowScale();

  const style: CSSProperties = {
    transform: `scale(${scale})`,
    transformOrigin: 'top left',
    width: `${CONTENT_WIDTH}px`,
    height: `${CONTENT_HEIGHT}px`
  };

  const returnTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  return (
    <main style={style}>
      <section className={styles.titleSection}>
        <h1>Piyoppi</h1>
        <p>Portfolio</p>
          <img
            src={togetherImg}
            alt="背景画像"
            className={styles.backgroundImage}
          />
      </section>

      <section className={styles.aboutSection}>
        <h2>About</h2>
        <div className={styles.textContainer}>
          <div className={styles.description}>
            愛知工業大学３年生
          </div>
        </div>
      </section>

      <section className={styles.stackSection}>
        <h2>Tech Stack</h2>
        <div className={styles.textContainer}>
          <div className={styles.description}>
            <div className={styles.iconContainer}>
              {techIcons.map((icon: { src: string; alt: string }, index: number) => (
                <img key={index} src={icon.src} alt={icon.alt} className={styles.icon} />
              ))}
            </div>
          </div>
        </div>
      </section>

      <section className={styles.productSection}>
        <h2>Products</h2>
        <BBKeyProduct />
        <BlenderProduct />
        <AITBomberProduct />
      </section>


      <section className={styles.endSection}>

        <header>ThisPage</header>

        <h2>概要</h2>
        <div className={styles.textContainer}>
            <div className={styles.description}>
            まさに、このページ、このポートフォリオです。私を構成する技術と創造力を一つのページにしました。
            </div>
        </div>
        <h2>使用技術</h2>
        <div className={styles.textContainer}>
            <div className={styles.description}>
            TypeScript, React, FramerMotion
            </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          style={{
            marginTop: '3rem',
            padding: '2rem 2rem',
            fontSize: '1.2rem',
            backgroundColor: 'rgba(255, 255, 255, 0.45)', 
            color: '#333',
            border: 'none',
            borderRadius: '20px',
            cursor: 'pointer',
            minWidth: '300px',
          }}
          onClick={returnTop}
        >
          <h2>ページ上部へもどる</h2>
        </motion.button>

      </section>
    </main>
  );
}

export default Main;
