import React, { useEffect, useRef } from 'react';

import styles from './Products.module.css';

import BBkeyImg1 from "../assets/productsImages/BBKey1.png";
import BBkeyImg2 from "../assets/productsImages/BBKey2.png";
import BBkeyImg3 from "../assets/productsImages/BBKey3.png";

type Props = {
  src: string;
  alt?: string;
};

const ImageFadeIn: React.FC<Props> = ({ src, alt }) => {
  return <img className="fadeIn" src={src} alt={alt ?? 'image'} />;
};

const BBKeyProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  return (
    <div ref={ref} style={{ height: "300vh", position: "relative" }}>
        {/* テキスト（画像と並行して左から出現） */}
        <div className={styles.leftFadeIn}>
          <header>BBKey</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              キーボード操作でお肉をひっくり返すゲームです。<br />
              BGMやイラストも私が用意しました。 <br />
              SysHack2025で制作しました。<br />
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              Unity, GitHub
            </div>
          </div>

        </div>


      <div className={styles.rightFadeIn}>
        <img
          src={BBkeyImg3}
          alt="BBkey画像3"
          style={{
            width: "50%",
            height: "auto",
            borderRadius: "30px",
          }}
        />
        <img
          src={BBkeyImg2}
          alt="BBkey画像2"
          style={{
            width: "50%",
            height: "auto",
            borderRadius: "30px",
          }}
        />
      </div>
    </div>
  );
};


export default BBKeyProduct;
