function Main() {
    return (
      <main style={{ 
        marginTop: '80px', // ← ヘッダー分の余白
        height: 'calc(100vh - 80px)', // ← 画面高 - ヘッダー高
        width: '100%',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <img 
          src="/images/together.png" 
          alt="背景画像" 
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover', // ← トリミングして全体にフィット
            position: 'absolute',
            top: 0,
            left: 0,
            zIndex: -1 // 背景にまわす
          }} 
        />
        <div style={{
          position: 'relative',
          color: '#fff',
          textAlign: 'center',
          paddingTop: '20%',
        }}>
          <h1>ようこそ！</h1>
          <p>ここにキャッチコピーや紹介文が入るよ</p>
        </div>
      </main>
    );
  }

  export default Main;
