import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import BBkeyImg from "../assets/productsImages/BBKey.png";

const BBKeyProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  // 進み具合に応じて x を制御
  const imageX = useTransform(
      scrollYProgress,
      [-0.5, 0.2, 0.5, 0.8],
      ['-100%', '0%', '0%', '-100%']
  );

  const imageY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    ["0%", "0", "200%"] // translateYの位置
  );

  // 進み具合に応じて x を制御
  const titleX = useTransform(
    scrollYProgress,
    [-0.5, 0.2, 0.5, 0.8],
    ['-100%', '0%', '0%', '-100%']
  );

  const titleY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    ["0%", "0", "200%"] // translateYの位置
  );

  return (
    <div ref={ref} style={{ height: "300vh", position: "relative" }}>
<motion.div
  style={{
    position: "absolute",
    top: "50%",
    width: "400px",
    borderRadius: "10px",
    x: imageX,
    y: imageY,
  }}
>
  <img
    src={BBkeyImg}
    alt="BBkey画像"
    style={{
      width: "200%",
      height: "auto",
      borderRadius: "10px",
    }}
  />
</motion.div>

<motion.div
  style={{
    position: "absolute",
    top: "60%", // 少しずらすと見やすくなる
    width: "400px",
    x: titleX,
    y: titleY,
  }}
>
  <header style={{ fontSize: "2rem", color: "#333" }}>
    BBKey
  </header>
</motion.div>
    </div>
  );
};


export default BBKeyProduct;
