function Main() {
    return (
      <main style={{ minHeight: '100vh' }}>
        {/* 全画面画像セクション */}
        <section style={{ 
          height: '100vh',
          width: '100%',
          backgroundImage: 'url("/images/together.png")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#fff',
          textAlign: 'center'
        }}>
          <h1>Piyoppi</h1>
          <p>Portfolio</p>
        </section>

        {/* 続きのコンテンツセクション */}
        <section style={{
          padding: '4rem 2rem',
          backgroundColor: '#f4f4f4',
          color: '#333',
        }}>
          <h2>次のセクションへようこそ</h2>
          <p>
            この部分にサイトの紹介や詳細を入れましょう。画像の下に現れることで、自然にスクロールできます。
          </p>
        </section>
      </main>
    );
  }

  export default Main;
