import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import ReactPlayer from 'react-player';


import AITBomberImg from "../assets/productsImages/AITBomber.png";

import styles from './Products.module.css';

const AITBomberProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  const videoRef = useRef<HTMLVideoElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const movieX = useTransform(
      scrollYProgress,
      [-0.5, 0.2, 0.8, 1],
      ['100%', '0%', '0%', '100%']
  );

  const movieY = useTransform(
    scrollYProgress,
    [0, 0.1, 0.8],
    [0, -500, 1500]
  );

  const imageOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.2, 0.8, 1],
    [0, 1, 1, 0]
  );

  const textX = useTransform(
    scrollYProgress,
    [-0.5, 0.3, 0.8, 1],
    ['-200%', '0%', '0%', '-200%']
  );

  const textY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.8],
    [0, 0, 1500]
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0.2, 0.3, 0.8, 1],
    [0, 1, 1, 0]
  );

  return (
    <div ref={ref} style={{ height: "200vh", position: "relative" }}>
        {/* テキスト（画像と並行して左から出現） */}
        <motion.div
          style={{
            position: 'absolute',
            left: '5%',
            x: textX,
            y: textY,
            opacity: textOpacity,
            fontWeight: 'bold',
            color: '#FFF',
          }}
        >
          <header>AITBomber</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              愛知工業大学の講義、「オブジェクト指向プログラミング及び演習２」<br />
              の中で制作したゲームです。時間が足らず、AITと言っておきながら<br />
              某爆弾男ゲームの丸パクリにしかならなかったのは残念でした。
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              javascript, HTML, CSS, Python, Flask, Socket.io, GitHub
            </div>
          </div>

        </motion.div>

        {/* イメージ */}
        <motion.div
        style={{
          width: "650px",
          height: "auto",
          borderRadius: "15px",
          opacity: imageOpacity,
          right: "0%",
          top: "15%",
          position: 'absolute',

          x: movieX,
          y: movieY
        }}
      >
        <img
          src={AITBomberImg}
          alt="AITBomberImg"
          style={{
            width: "100%",
            height: "auto",
            borderRadius: "30px",
          }}
        />
      </motion.div>

      {/* ReactPlayer 動画 */}
      <motion.div
        style={{
          width: "500px",
          height: "280px",
          borderRadius: "5px",
          opacity: imageOpacity,
          right: "0%",
          top: "0%",
          position: 'absolute',
          x: movieX,
          y: movieY
        }}
      >
        <ReactPlayer
          url="/videos/AITBomber.mp4"
          playing
          loop
          muted
          controls
          width="100%"
          height="100%"
          style={{
            borderRadius: "10px",
            overflow: "hidden"
          }}
        />
      </motion.div>

    </div>
  );
};


export default AITBomberProduct;
