import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

import styles from './Products.module.css';

const BlenderProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  const videoRef = useRef<HTMLVideoElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const movieX = useTransform(
      scrollYProgress,
      [-0.5, 0.2, 0.8, 1],
      ['-100%', '0%', '0%', '-100%']
  );

  const movieY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.8, 1],
    ['0%', '50%', '50%', '100%']
  );

  const movieOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.2, 0.8, 1],
    [0, 1, 1, 0]
  );

  const textX = useTransform(
    scrollYProgress,
    [-0.1, 0.3, 0.8, 1],
    ['200%', '0%', '0%', '200%']
  );

  const textY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.8, 1],
    [0, 50, 50, 100]
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0.2, 0.3, 0.8, 1],
    [0, 1, 1, 0]
  );

  return (
    <div ref={ref} style={{ height: "200vh", position: "relative" }}>
        {/* テキスト（画像と並行して左から出現） */}
        <motion.div
          style={{
            position: 'absolute',
            right: '5%',
            x: textX,
            y: textY,
            opacity: textOpacity,
            fontWeight: 'bold',
            color: '#FFF',
          }}
        >
          <header>Blender</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              Blenderで作った3D作品集です。
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              Blender
            </div>
          </div>

        </motion.div>


      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          opacity: movieOpacity,
          left: "10%",
          top: "0%",
          position: 'absolute',
          x: movieX,
          y: movieY
        }}
      >
        <video
          autoPlay
          muted
          loop
          playsInline
          ref={videoRef}
          style={{
            width: '100%',
            height: '100%',
            borderRadius: '10px',
            objectFit: 'cover'
          }}
        >
        <source src="/videos/encodedEntei.mp4" type="video/mp4" />
        <p>Your browser does not support the video tag.</p>
        </video>
      </motion.div>


    </div>
  );
};


export default BlenderProduct;
