import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

import styles from './Products.module.css';

const ThisPageProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  const videoRef = useRef<HTMLVideoElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const textY = useTransform(
    scrollYProgress,
    [0.2, 1],
    [0, 1000]
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0.2, 1],
    [0, 1]
  );

  return (
    <div ref={ref} style={{ height: "100vh", position: "relative" }}>
        {/* テキスト（画像と並行して左から出現） */}
        <motion.div
          style={{
            position: 'absolute',
            y: textY,
            opacity: textOpacity,
            fontWeight: 'bold',
            color: '#FFF',
          }}
        >
          <header>ThisPage</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              まさに、このページ、このポートフォリオです。私を構成する技術と創造力を一つのページにしました。
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              TypeScript, React, FramerMotion
            </div>
          </div>

        </motion.div>

    </div>
  );
};


export default ThisPageProduct;
