import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import ReactPlayer from 'react-player';


import AITBomberImg from "../assets/productsImages/AITBomber.png";

import styles from './Products.module.css';

const AITBomberProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  const videoRef = useRef<HTMLVideoElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const movieX = useTransform(
      scrollYProgress,
      [0, 0.5, 0.8, 1],
      ['100%', '0%', '0%', '100%']
  );

  const movieY = useTransform(
    scrollYProgress,
    [0, 0.1, 0.8],
    [0, -500, 1500]
  );

  const imageOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.5, 0.8, 1],
    [0, 1, 1, 0]
  );

  const textX = useTransform(
    scrollYProgress,
    [0, 0.5, 0.8, 1],
    ['-200%', '0%', '0%', '-200%']
  );

  const textY = useTransform(
    scrollYProgress,
    [0, 0.1, 0.8],
    [0, -500, 1500]
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0, 0.5, 0.7, 1],
    [0, 1, 1, 0]
  );

  return (
    <div ref={ref} className={styles.productContainer}>
        {/* テキスト（画像と並行して左から出現） */}
        <div className={styles.leftFadeIn}>
          <header>AITBomber</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              愛知工業大学の講義、「オブジェクト指向プログラミング及び演習２」<br />
              の中で制作したゲームです。時間が足らず、AITと言っておきながら<br />
              某爆弾男ゲームの丸パクリにしかならなかったのは残念でした。
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              javascript, HTML, CSS, Python, Flask, Socket.io, GitHub
            </div>
          </div>
        </div>

        <div className={styles.rightFadeIn}>
          {/* イメージ */}
          <img
            src={AITBomberImg}
            alt="AITBomberImg"
            style={{
              width: "80%",
              height: "auto",
              borderRadius: "30px",
            }}
          />

        {/* ReactPlayer 動画 */}
          <ReactPlayer
            url="/videos/AITBomber.mp4"
            playing
            loop
            muted
            controls
            width="110%"
            height="auto"
            style={{
              borderRadius: "10px",
              overflow: "hidden"
            }}
          />
        </div>

    </div>
  );
};


export default AITBomberProduct;
