import React, { useEffect, useRef, memo, FC } from 'react';

import styles from './Main.module.css';
import togetherImg from "../assets/images/together.png";
import htmlIcon from "../assets/stackIcons/HTML.png";


type Props = {
  canvasWidth: number; //canvas幅
  canvasHeight: number; //canvas高さ
  x: number; //矩形始点x
  y: number; //矩形始点y
  rectWidth: number; //矩形幅
  rectHeight: number; //矩形高さ
};


const Main: React.FC = () => {
  return (
    <main>
      <section className={styles.titleSection}>
      <h1>Piyoppi</h1>
      <p>Portfolio</p>
        <img
          src={togetherImg}
          alt="背景画像"
          className={styles.backgroundImage}
        />
      </section>

      <section className={styles.aboutSection}>
        <h2>About</h2>
        <div className={styles.textContainer}>
          <div className={styles.description}>
            愛知工業大学３年生
          </div>
        </div>
      </section>

      <section className={styles.stackSection}>
        <h2>Tech Stack</h2>
        <div className={styles.textContainer}>
          <div className={styles.description}>
            フロントエンド
            <a href="https://x.com/hiy_ait_kk" target="_blank" rel="noopener noreferrer">
              <img src={htmlIcon} alt="Twitter" className={styles.htmlIcon} />
            </a>
            <a href="https://www.instagram.com/hi_n_sh/" target='_blank' rel="noopener noreferrer">
              <img src={instaLogo} alt="Instagram" className={styles.instagramIcon} />
            </a>
          </div>
        </div>
      </section>
    </main>
  );
}

export default Main;
