import React, { useEffect, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

import styles from './Products.module.css';

import BBkeyImg3 from "../assets/productsImages/BBKey3.png";

import Entei_mp4 from "../assets/productsImages/Entei.MP4";

const BlenderProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  const videoRef = useRef<HTMLVideoElement>(null);
  useEffect(() => {
      videoRef.current?.play();
  }, []);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const movieX = useTransform(
      scrollYProgress,
      [-0.5, 0.2, 0.5, 0.8],
      ['-100%', '0%', '0%', '-100%']
  );

  const movieY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    [100, 100, 1000]
  );

  const imageOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.2, 0.5, 0.8],
    [0, 1, 1, 0]
  );

  const textX = useTransform(
    scrollYProgress,
    [0.05, 0.3, 0.5, 0.8],
    ['200%', '0%', '0%', '200%']
  );

  const textY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    [0, 0, 1000]
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0.2, 0.3, 0.5, 0.8],
    [0, 1, 1, 0]
  );

  return (
    <div ref={ref} style={{ height: "300vh", position: "relative" }}>
        {/* テキスト（画像と並行して左から出現） */}
        <motion.div
          style={{
            position: 'absolute',
            right: '5%',
            x: textX,
            y: textY,
            opacity: textOpacity,
            fontWeight: 'bold',
            color: '#FFF',
          }}
        >
          <header>Blender</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              キーボード操作でお肉をひっくり返すゲームです。
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              Blender
            </div>
          </div>

        </motion.div>


      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          opacity: imageOpacity,
          left: "0%",
          top: "18%",
          position: 'absolute',

          x: movieX,
          y: movieY
        }}
      >

        <video controls muted ref={videoRef} >
            <source src={Entei_mp4} type="video/mp4" />
            <p>Your browser doesn't support HTML5 video.</p>
        </video>

      </motion.div>

      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          opacity: imageOpacity,
          left: "0%",
          top: "18%",
          position: 'absolute',

          x: movieX,
          y: movieY
        }}
      >
        <img
          src={BBkeyImg3}
          alt="BBkey画像"
          style={{
            width: "100%",
            height: "auto",
            borderRadius: "30px",
          }}
        />
      </motion.div>


    </div>
  );
};


export default BlenderProduct;
