import React, { useEffect, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import BBkeyImg from "../assets/productsImages/BBKey.png";

const BBKeyProduct: React.FC = () => {

  useEffect(() => {
    imageY.on("change", (latest) => {
      console.log("imageY:", latest);
    });
  }, []);

  const ref = useRef<HTMLDivElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const imageX = useTransform(
      scrollYProgress,
      [-0.5, 0.2, 0.5, 0.8],
      ['-100%', '0%', '0%', '-100%']
  );

  const imageY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    ["0%", "0", "200%"]
  );

  const imageOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.2, 0.5, 0.8],
    [0, 1, 1, 0]
  );

  const textX = useTransform(
    scrollYProgress,
    [-0.5, 0.2, 0.5],
    ['200%', '0%', '200%']
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.2, 0.5],
    [0, 1, 0]
  );

  return (
    <div ref={ref} style={{ height: "300vh", position: "absolute" }}>
        {/* テキスト（画像と並行して左から出現） */}
      <motion.div
        style={{
          position: 'absolute',
          top: '20%',
          left: '95%',
          x: textX,
          y: imageY,
          opacity: textOpacity,
          fontSize: '2rem',
          fontWeight: 'bold',
          color: '#333',
          background: 'white',
          padding: '1rem 2rem',
          borderRadius: '20px',
          boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
        }}
      >
        BBKey
      </motion.div>

      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          opacity: imageOpacity,
          left: "0%",
          top: "1%",
          position: 'absolute',

          x: imageX,
          y: imageY
        }}
      >
        <img
          src={BBkeyImg}
          alt="BBkey画像"
          style={{
            width: "200%",
            height: "auto",
            borderRadius: "30px",
          }}
        />
      </motion.div>
    </div>
  );
};


export default BBKeyProduct;
