import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

import styles from './Products.module.css';

const ThisPageProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  const videoRef = useRef<HTMLVideoElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const movieX = useTransform(
      scrollYProgress,
      [0.2, 0.5, 0.8, 1],
      ['-100%', '0%', '0%', '-100%']
  );

  const movieY = useTransform(
    scrollYProgress,
    [0.2, 0.25, 1],
    [0, 0, 1400]
  );

  const movieOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.5, 0.8, 1],
    [0, 1, 1, 0]
  );

  const textX = useTransform(
    scrollYProgress,
    [0.2, 0.5, 0.8, 1],
    ['200%', '0%', '0%', '200%']
  );

  const textY = useTransform(
    scrollYProgress,
    [0.2, 0.25, 1],
    [0, 0, 1400]
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0.2, 0.3, 0.8, 1],
    [0, 1, 1, 0]
  );

  return (
    <div ref={ref} style={{ height: "150vh", position: "relative" }}>
        {/* テキスト（画像と並行して左から出現） */}
        <motion.div
          style={{
            position: 'absolute',
            right: '5%',
            x: textX,
            y: textY,
            opacity: textOpacity,
            fontWeight: 'bold',
            color: '#FFF',
          }}
        >
          <header>ThisPage</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              まさに、このページ、このポートフォリオです。
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              Blender
            </div>
          </div>

        </motion.div>

    </div>
  );
};


export default ThisPageProduct;
