import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import BBkeyImg from "../assets/productsImages/BBKey.png";

const BBKeyProduct: React.FC = () => {

    const ref = useRef<HTMLDivElement>(null);

    // この要素に対してスクロールを取得
    const { scrollYProgress } = useScroll({
        target: ref,
        offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
    });

    // 進み具合に応じて x を制御
    const x = useTransform(
        scrollYProgress,
        [-0.5, 0.2, 0.5, 0.8],
        ['-100%', '0%', '0%', '-100%']
    );

  const y = useTransform(
    scrollYProgress,
    [0, 0.2, 1.0],
    ["0%", "0", "200%"] // translateYの位置
  );
  return (
    <div ref={ref} style={{ height: "300vh", position: "relative" }}>
    <motion.div
      style={{
        width: "400px",
        height: "auto",
        borderRadius: "10px",
        left: "0%",
        transform: "translateY(-50%)",

        x: x,
        y: y
      }}
    >
      <img
        src={BBkeyImg}
        alt="BBkey画像"
        style={{
          width: "200%",
          height: "auto",
          borderRadius: "10px",
        }}
      />
    </motion.div>
  </div>
  );
};


export default BBKeyProduct;
