import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import BBkeyImg from "../assets/productsImages/BBKey.png";

const BBKeyProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  // 進み具合に応じて x を制御
  const imageX = useTransform(
      scrollYProgress,
      [-0.5, 0.2, 0.5, 0.8],
      ['-100%', '0%', '0%', '-100%']
  );

  const imageY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    ["0%", "0", "200%"] // translateYの位置
  );

  // 進み具合に応じて x を制御
  const titleX = useTransform(
    scrollYProgress,
    [-0.5, 0.2, 0.5, 0.8],
    ['-100%', '0%', '0%', '-100%']
  );

  const titleY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    ["0%", "0", "200%"] // translateYの位置
  );

  return (
    <div ref={ref} style={{ height: "300vh", position: "relative" }}>
      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          left: "0%",
          transform: "translateY(-50%)",

          x: imageX,
          y: imageY
        }}
      >
        <img
          src={BBkeyImg}
          alt="BBkey画像"
          style={{
            width: "200%",
            height: "auto",
            borderRadius: "10px",
          }}
        />
      </motion.div>
      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          left: "0%",
          transform: "translateY(-50%)",

          x: imageX,
          y: imageY
        }}
      >
        <title>
          BBKey
        </title>
      </motion.div>
    </div>
  );
};


export default BBKeyProduct;
