import React, { useEffect, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

import styles from './Products.module.css';

import BBkeyImg1 from "../assets/productsImages/BBKey1.png";
import BBkeyImg2 from "../assets/productsImages/BBKey2.png";
import BBkeyImg3 from "../assets/productsImages/BBKey3.png";

const BBKeyProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  // この要素に対してスクロールを取得
  const { scrollYProgress } = useScroll({
      target: ref,
      offset: ["start end", "end start"], // refの「スタート位置」と「エンド位置」に合わせる
  });

  const image1X = useTransform(
      scrollYProgress,
      [-0.5, 0.2, 0.5, 0.8],
      ['-100%', '0%', '0%', '-100%']
  );

  const image1Y = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    [100, 100, 1000]
  );

  const imageOpacity = useTransform(
    scrollYProgress,
    [0.15, 0.2, 0.5, 0.8],
    [0, 1, 1, 0]
  );

  const textX = useTransform(
    scrollYProgress,
    [0.05, 0.3, 0.5, 0.8],
    ['200%', '0%', '0%', '200%']
  );

  const textY = useTransform(
    scrollYProgress,
    [0, 0.2, 0.5],
    [0, 0, 1000]
  );

  const textOpacity = useTransform(
    scrollYProgress,
    [0.2, 0.3, 0.5, 0.8],
    [0, 1, 1, 0]
  );

  return (
    <div ref={ref} style={{ height: "300vh", position: "relative" }}>
        {/* テキスト（画像と並行して左から出現） */}
        <motion.div
          style={{
            position: 'absolute',
            right: '5%',
            x: textX,
            y: textY,
            opacity: textOpacity,
            fontSize: '15rem',
            fontWeight: 'bold',
            color: '#FFF',
          }}
        >
        <header>BBKey</header>

        <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              キーボード操作でお肉をひっくり返すゲームです。<br />
              SysHack2025で制作したもので、BGMやイラストも私が用意しました。
            </div>
          </div>

        </motion.div>

      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          opacity: imageOpacity,
          left: "0%",
          top: "18%",
          position: 'absolute',

          x: image1X,
          y: image1Y
        }}
      >
        <img
          src={BBkeyImg3}
          alt="BBkey画像"
          style={{
            width: "100%",
            height: "auto",
            borderRadius: "30px",
          }}
        />
      </motion.div>

      <motion.div
        style={{
          width: "400px",
          height: "auto",
          borderRadius: "10px",
          opacity: imageOpacity,
          left: "0%",
          top: "-3%",
          position: 'absolute',

          x: image1X,
          y: image1Y
        }}
      >
        <img
          src={BBkeyImg2}
          alt="BBkey画像"
          style={{
            width: "200%",
            height: "auto",
            borderRadius: "30px",
          }}
        />
      </motion.div>
    </div>
  );
};


export default BBKeyProduct;
