function Main() {
    return (
    <main style={{ minHeight: '100vh' }}>
        {/* 全画面画像セクション */}
        <section style={{ 
          height: '100vh',
          width: '100%',
          position: 'relative',
          overflow: 'hidden'
        }}>
          <img 
            src="/images/together.png" 
            alt="背景画像" 
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              position: 'absolute',
              top: 0,
              left: 0,
              zIndex: -1
            }} 
          />
          <div style={{
            position: 'relative',
            color: '#fff',
            textAlign: 'center',
            paddingTop: '20%',
          }}>
            <h1>Piyoppi</h1>
            <p>Portfolio</p>
          </div>
        </section>

        {/* 続きのコンテンツセクション */}
        <section style={{
          padding: '4rem 2rem',
          backgroundColor: '#f4f4f4',
          color: '#333',
        }}>
          <h2>次のセクションへようこそ</h2>
          <p>
            この部分にサイトの紹介や詳細を入れましょう。画像の下に現れることで、自然にスクロールできます。
          </p>
        </section>
      </main>
    );
  }

  export default Main;
