import React, { useRef } from 'react';
import ReactPlayer from 'react-player';


import AITBomberImg from "../assets/productsImages/AITBomber.png";

import styles from './Products.module.css';

const AITBomberProduct: React.FC = () => {

  const ref = useRef<HTMLDivElement>(null);

  const videoRef = useRef<HTMLVideoElement>(null);

  return (
    <div ref={ref} className={styles.productContainer}>
        {/* テキスト（画像と並行して左から出現） */}
        <div className={styles.leftFadeIn}>
          <header
          style={{fontSize: '10rem'}}>AITBomber</header>

          <h2>概要</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              講義、「オブジェクト指向プログラミング及び演習２」<br />
              の中で制作したゲームです。時間が足らず、<br />AITと言っておきながら
              某爆弾男ゲームの丸パクリに<br />しかならなかったのは残念でした。
            </div>
          </div>
          <h2>使用技術</h2>
          <div className={styles.textContainer}>
            <div className={styles.description}>
              javascript, HTML, CSS, Python, Flask, Socket.io, GitHub
            </div>
          </div>
        </div>


        <div className={styles.rightFadeIn}>
          {/* ReactPlayer 動画 */}
          <ReactPlayer
            url="/videos/AITBomber.mp4"
            playing
            loop
            muted
            controls
            style={{
              height: "auto",
              width: "10%",
              borderRadius: "10%",
              overflow: "hidden"
            }}
          />

          {/* イメージ */}
          <img
            src={AITBomberImg}
            alt="AITBomberImg"
            style={{
              width: "80%",
              borderRadius: "30px",
            }}
          />

        </div>

    </div>
  );
};


export default AITBomberProduct;
