import React from 'react';
const Header: React.FC = () => {
  return (
    <header style={{
    backgroundColor: 'rgba(51, 51, 51, 0.7)', // 黒っぽい色に70%不透明
    color: '#fff',
    padding: '1rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    position: 'fixed',  // スクロールしても上に固定したい場合
    top: 0,
    width: '100%',
    backdropFilter: 'blur(4px)', // 背景をちょっとぼかすとより美しい✨
    }}>
      <div>
        <h1>My Website</h1>
        <nav>
          <ul style={{ display: 'flex', listStyle: 'none', gap: '1rem', margin: 0, padding: 0 }}>
          </ul>
        </nav>
      </div>
      <a href="https://x.com/hiy_ait_kk" target="_blank" rel="noopener noreferrer">
        <img 
            src="/images/xlogo.png" 
            alt="Twitter" 
            style={{ width: '24px', height: '24px' }} 
        />
      </a>
    </header>


  );
};

export default Header;
