function Main() {
    return (
      <main>
        {/* 画像＋テキストセクション */}
        <section style={{ 
          width: '100%', 
          position: 'relative', 
          overflow: 'hidden', 
          paddingTop: '256px',
        }}>
          {/* 背景画像 */}
          <img
            src="/images/together.png"
            alt="背景画像"
            style={{
              width: '100%',
              height: 'auto',
              display: 'block',
            }}
          />

          {/* 中央テキスト */}
          <div style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            color: '#fff',
            textAlign: 'center',
            textShadow: '0 0 10px rgba(0,0,0,0.7)',
          }}>
            <h1>Piyoppi</h1>
            <p>Portfolio</p>
          </div>
        </section>

        {/* 続きのセクション */}
        <section style={{
          padding: '4rem 2rem',
          backgroundColor: '#5C849E',
          color: '#fff',
        }}>
          <h2>次のセクションへようこそ</h2>
          <p>
            この部分にサイトの紹介や詳細を入れましょう。
          </p>
        </section>
      </main>
    );
  }

  export default Main;
