function Main() {
    return (
      <main style={{ backgroundColor: '#000' }}>
        {/* 画像セクション（高さは画像サイズにまかせる） */}
        <section style={{ 
          width: '100%',
          backgroundImage: 'url("/images/together.png")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          padding: '8rem 2rem', // 上下に余白をつけて調整
          color: '#fff',
          textAlign: 'center',
        }}>
          <h1>Piyoppi</h1>
          <p>Portfolio</p>
        </section>

        {/* 続きのセクション */}
        <section style={{
          padding: '4rem 2rem',
          backgroundColor: '#5C849E',
          color: '#fff',
        }}>
          <h2>次のセクションへようこそ</h2>
          <p>
            この部分にサイトの紹介や詳細を入れましょう。
          </p>
        </section>
      </main>
    );
  }

  export default Main;
